#!/usr/bin/env python3

import base64, struct

n = 10240099
b1 = (n & 0xff000000) >> 24
b2 = (n & 0xff0000) >> 16
b3 = (n & 0xff00) >> 8
b4 = n & 0xff
bs = bytes([b1, b2, b3, b4])
print(bs)

print(struct.pack('>I', 10240099))
print(struct.unpack('>IH', b'\xf0\xf0\xf0\xf0\x80\x80'))

bmp_data = base64.b64decode('')

def bmp_info(data):
  result = struct.unpack('<ccIIIIIIHH', data[:30])
  return {
    'width': result[6],
    'height': result[7],
    'color': result[9]
  }


bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')